    def clear_label(self):
       self.lebel_natija.setText("")


    def update_label(self, number):
        current_text = self.lebel_natija.text()

        if current_text=="0" or self.tekshirish:
                self.lebel_natija.setText(number)
                self.tekshirish=False
        else:
             self.lebel_natija.setText(current_text + number)
    
    def calculate_result(self):
        try:
            result = eval(self.lebel_natija.text())
            self.lebel_natija.setText(str(result))
            self.tekshirish=True
        except Exception as e:
            self.lebel_natija.setText("Error")

