    def foiz_chiqar(self):
        foizda=eval(self.lebel_natija.text())
        self.lebel_natija.setText(str(foizda/100))
    
    def kvadratga_kotar_funk(self):
        self.soni = float(self.lebel_natija.text())
        self.lebel_natija.setText(str(self.soni ** 2))



    def bittalab_ochir(self):
        current_text = self.lebel_natija.text()
        if current_text:
           self.lebel_natija.setText(current_text[:-1])

    def taqsim_bolishli(self):
        try:
           current_value = float(self.lebel_natija.text())
           if current_value != 0:
               self.lebel_natija.setText(str(1/current_value)) 
           else:
               self.lebel_natija.setText("Error")
        except Exception as e:
           self.lebel_natija.setText("Error")


